import 'dart:collection';import 'package:flutter/material.dart';import 'package:watleuk/controller/translation_controller.dart';class MainTranslationScreen extends StatefulWidget {  _TranslatorState _currentAppState;  MainTranslationScreen(TranslationController _translationController, {Key key})      : super(key: key) {    _currentAppState = new _TranslatorState(_translationController);  }  @override  _TranslatorState createState() => _currentAppState;}class _TranslatorState extends State<MainTranslationScreen> {  TranslationController _translationController;  String baseText = '';  String translatedText = '';  _TranslatorState(TranslationController translationController) {    _translationController = translationController;    baseText = translationController.getCurrentTranslation().baseText;  }  @override  Widget build(BuildContext context) {    final double _width = MediaQuery.of(context).size.width * 0.95;    return new Scaffold(      body: new Column(          mainAxisAlignment: MainAxisAlignment.spaceEvenly,          children: <Widget>[            new Row(              mainAxisAlignment: MainAxisAlignment.center,              children: <Widget>[                new Column(                  mainAxisAlignment: MainAxisAlignment.end,                  children: <Widget>[                    new Container(                      padding: const EdgeInsets.all(16.0),                      width: _width,                      child: new Text(                        baseText,                        textAlign: TextAlign.center,                        style: TextStyle(                            fontSize: 22.0, fontWeight: FontWeight.bold),                      ),                    ),                    new Container(                      padding: const EdgeInsets.all(16.0),                      width: _width,                      child: new Text(                        translatedText,                        textAlign: TextAlign.center,                        style: TextStyle(                            fontSize: 20.0, fontWeight: FontWeight.normal),                      ),                    ),                  ],                ),              ],            ),          ]),      bottomNavigationBar: new ButtonBar(        alignment: MainAxisAlignment.end,        children: <Widget>[          new RaisedButton(            onPressed: unmask,            child: IconButton(icon: Icon(Icons.remove_red_eye)),          ),          new RaisedButton(              color: Colors.green[400],              onPressed: nextTranslation,              child: IconButton(                color: Colors.black,                icon: Icon(Icons.navigate_next),              )),        ],      ),    );  }  void unmask() {    setState(() {      translatedText =          _translationController.getCurrentTranslation().translatedText;    });  }  void nextTranslation() {    setState(() {      baseText = _translationController.getNextTranslation().baseText;      translatedText = '';    });  }}