import 'dart:collection';import 'package:flutter/material.dart';import 'package:watleuk/controller/translation_controller.dart';class MainTranslationScreen extends StatefulWidget {  _TranslatorState currentAppState;  MainTranslationScreen(TranslationController translationController, {Key key})      : super(key: key) {    currentAppState = new _TranslatorState(translationController);  }  @override  _TranslatorState createState() => currentAppState;}class _TranslatorState extends State<MainTranslationScreen> {  TranslationController _translationController;  String baseText = '';  String translatedText = '';  _TranslatorState(TranslationController translationController) {    _translationController = translationController;    baseText = translationController.getCurrentTranslation().baseText;  }  @override  Widget build(BuildContext context) {    return new Column(        mainAxisAlignment: MainAxisAlignment.spaceEvenly,        children: <Widget>[          new Row(            mainAxisAlignment: MainAxisAlignment.center,            children: <Widget>[              new Column(                mainAxisAlignment: MainAxisAlignment.end,                children: <Widget>[                  new Text(                    baseText,                    textAlign: TextAlign.center,                    style:                        TextStyle(fontSize: 18.0, fontWeight: FontWeight.bold),                  ),                  new Text(                    translatedText,                    textAlign: TextAlign.center,                    style: TextStyle(                        fontSize: 18.0, fontWeight: FontWeight.normal),                  ),                ],              ),            ],          ),          new Row(            mainAxisAlignment: MainAxisAlignment.end,            children: <Widget>[              new ButtonBar(                alignment: MainAxisAlignment.spaceEvenly,                mainAxisSize: MainAxisSize.max,                children: <Widget>[                  new RaisedButton(                    onPressed: unmask,                    child: IconButton(icon: Icon(Icons.remove_red_eye)),                  ),                  new RaisedButton(                      color: Colors.green[400],                      onPressed: nextTranslation,                      child: IconButton(                        color: Colors.black,                        icon: Icon(Icons.navigate_next),                      )),                ],              ),            ],          ),        ]);  }  void unmask() {    setState(() {      translatedText =          _translationController.getCurrentTranslation().translatedText;    });  }  void nextTranslation() {    setState(() {      baseText = _translationController.getNextTranslation().baseText;      translatedText = '';    });  }}