import 'package:flutter/material.dart';class FavouriteStarIconButton extends StatefulWidget {  FavouriteStarIconButton({Key key, @required this.onChanged})      : super(key: key);  final ValueChanged<bool> onChanged;  _FavouriteStarIconButtonState createState() =>      _FavouriteStarIconButtonState();}class _FavouriteStarIconButtonState extends State<FavouriteStarIconButton> {  bool _highlight = false;  _FavouriteStarIconButtonState();  Widget build(BuildContext context) {    return new FlatButton(      onPressed: handleStarPress,      child: IconButton(          icon: _highlight              ? Icon(                  Icons.star,                  color: Colors.yellow[400],                )              : Icon(Icons.star_border)),    );  }  void handleStarPress() {    setState(() {      _highlight = !_highlight;      widget.onChanged(_highlight);    });  }}